define([
	"dojo",
	"doh",
	//tested libraries
	"gjax/_base/dom",
	"dojo/dom-construct"

], function(dojo, doh, gDom, domConstruct) {

	var domTest = {
		getText : function() {
			var node = domConstruct.create("div");
			node.innerHTML = "a<span>b</span>c";/* git-qa */
			doh.is("abc", gDom.getText(node));

			node.innerHTML = "abc";/* git-qa */
			doh.is("abc", gDom.getText(node));

			node.innerHTML = "a<span>b</span>c<span><span>d</span>e</span>f";/* git-qa */
			doh.is("abcdef", gDom.getText(node));
		},
		setText : function() {
			var node = domConstruct.create("div");

			gDom.setText(node, "abc");
			doh.is("abc", node.innerHTML);/* git-qa */

			gDom.setText(node, "ab<span>c</span>");
			doh.is("ab&lt;span&gt;c&lt;/span&gt;", node.innerHTML); /* git-qa */
		}
	};

	doh.register("gjax/_base/dom", domTest);
});
